mapper.update:
update pcp_member
set nickname=#{nickname},phone=#{phone},birth=#{birth, jdbcType=VARCHAR},
	email=#{email, jdbcType=VARCHAR},addr=#{addr, jdbcType=VARCHAR},
	grade=#{grade},mfile=#{mfile, jdbcType=VARCHAR},msfile=#{msfile, jdbcType=VARCHAR}
where mid=#{mid}

mapper.select:
select mid 
from pcp_member 
where name=#{name} 
	and email=#{email}

	and mid=#{mid}

mapper.update:
update pcp_member
set pass=#{pass}
where mid=#{mid}
